        #region Gun Thingy
        public void SortToLists()
        {
            string[] lines = System.IO.File.ReadAllLines(@"C:\Users\K.Kisielinski\Source\Repos\KursALX\ShootingRangeForms\TxtFiles\GunList.txt");
            foreach (string line in lines)
            {
                string[] gunData = line.Split('/');
                ClassifyGun(gunData[0], gunData[1], gunData[2], double.Parse(gunData[3]), int.Parse(gunData[4]));
            }
        }
        private void ObjectListCreate(List<GunHolder> gunsList)
        {
            GunsPresent = new List<GunPresentBox>();
            foreach(GunHolder gun in gunsList)
            {
                GunsPresent.Add(new GunPresentBox(gun));
            }
        }
        private void ClassifyGun(string name, string description, string image, double price, int type)
        {
            GunHolder gun = new GunHolder(name, description, image, price);
            switch (gun.Category)
            {
                case CategoryEnum.HANDGUNS:
                    HandGuns.Add(gun);
                    break;
                case CategoryEnum.REVOLVERS:
                    Revolvers.Add(gun);
                    break;
                case CategoryEnum.RIFLES:
                    Rifles.Add(gun);
                    break;
                case CategoryEnum.SHOTGUNS:
                    Shotguns.Add(gun);
                    break;
                case CategoryEnum.MACHINEGUNS:
                    MachineGuns.Add(gun);
                    break;
                case CategoryEnum.SNIPERRIFLES:
                    SniperRifles.Add(gun);
                    break;
            }
        }
        public void GunAddToCart(object sender, EventArgs e, GunHolder GunUsed)
        {
            bool ObjectExistsInCart = false;
            foreach (var gunItem in gunCart.GunsWillBuy.Select((value, i) => new { i, value }))
            {
                GunHolder gun = gunItem.value;
                int i = gunItem.i;
                if (gun.Name == GunUsed.Name)
                {
                    gunCart.GunsWillBuy[i].Amount += GunUsed.Amount;
                    ObjectExistsInCart = true;
                }
            }
            if (!ObjectExistsInCart)
            {
                gunCart.GunsWillBuy.Add(GunUsed);
            }
        }
